import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
} from "firebase/auth";
import { useState } from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  TextInput,
  TouchableOpacity,
} from "react-native";

const auth = getAuth();

const Authentication: React.FC<{}> = ({ navigation }) => {
  onAuthStateChanged(auth, (user) => {
    if (user != null) {
      navigation.navigate("Home");
    }

    // Do other things
  });
  const { container, topImage, formContainer } = styles;
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const disableButtons = !email || !password;

  const login = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        console.log(userCredential);
        const user = userCredential.user;
        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
      });
  };

  const register = () => {
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        console.log(userCredential);
        const user = userCredential.user;
        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
      });
  };

  return (
    <View style={container}>
      <Image style={topImage} source={require("../../assets/helpearn.jpeg")} />
      <View style={formContainer}>
        <TextInput
          style={styles.input}
          onChangeText={setEmail}
          value={email}
          placeholder="Email"
        />
        <TextInput
          style={styles.input}
          onChangeText={setPassword}
          value={password}
          placeholder="Password"
          autoCapitalize={"none"}
          keyboardType="default"
          secureTextEntry
        />
        <TouchableOpacity
          style={styles.button}
          onPress={login}
          activeOpacity={0.8}
          disabled={disableButtons}
        >
          <Text>Login</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.button, styles.registerButton]}
          onPress={register}
          activeOpacity={0.8}
          disabled={disableButtons}
        >
          <Text style={styles.buttonText}>Register</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: "relative",
    alignItems: "center",
  },
  topImage: {
    height: 800,
    width: 500,
  },
  input: {
    height: 50,
    borderRadius: 15,
    margin: 12,
    borderWidth: 1,
    paddingHorizontal: 10,
    paddingVertical: 5,
    width: 300,
    color: "white",
    textShadowColor: "white",
    backgroundColor: "#ffb3b3",
  },
  registerButton: { backgroundColor: "#800000", marginTop: 20 },
  buttonText: { fontSize: 20, color: "white" },
  button: {
    height: 50,
    width: 160,
    borderRadius: 20,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#ffe6e6",
  },

  formContainer: {
    textAlign: "center",
    alignContent: "center",
    top: 0,
    bottom: 0,
    right: 50,
    left: 50,
    justifyContent: "center",
    alignItems: "center",
    position: "absolute",
    backgroundColor: "transparent",
  },
});

export default Authentication;
